package myproject;

public class unary_op_class1 {

	//public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//class OperatorExample{  
			
	
	        public static void main(String args[])
	        
	        {  
	        	
	        	
			int x=10;  
			System.out.println(x--);  //first prints 10 and then updates to( 9)
	        System.out.println(++x);  //now 9 gets updated to 10 and prints as(10) 
			System.out.println(x++);  //now 10 gets printed as it is 10, and then later increments to (11)
			System.out.println(--x); // now 11 gets down to 10 and then prints as (10)
			System.out.println(--x); // now 10 gets down to 9 and then prints as 9
			  
		
		
		
		}

}
